import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

axios.defaults.baseURL = API_URL;

export const authAPI = {
  login: (credentials) => axios.post('/auth/login', credentials),
  register: (userData) => axios.post('/auth/register', userData),
  getMe: () => axios.get('/auth/me'),
};

export const assignmentAPI = {
  getAssignments: () => axios.get('/assignments'),
  getAssignment: (id) => axios.get(`/assignments/${id}`),
  createAssignment: (data) => axios.post('/assignments', data),
  updateAssignment: (id, data) => axios.put(`/assignments/${id}`, data),
  deleteAssignment: (id) => axios.delete(`/assignments/${id}`),
};

export const submissionAPI = {
  getSubmissions: () => axios.get('/submissions'),
  getMySubmissions: () => axios.get('/submissions/my-submissions'),
  submitAssignment: (assignmentId, formData) => 
    axios.post(`/submissions/${assignmentId}`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' }
    }),
  gradeSubmission: (id, data) => axios.put(`/submissions/${id}/grade`, data),
};
