import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Row, Col, Alert, Badge } from 'react-bootstrap';
import { assignmentAPI } from '../services/api';
import { useAuth } from '../utils/AuthContext';

const Assignments = () => {
  const [assignments, setAssignments] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    dueDate: '',
    maxPoints: 100,
    instructions: '',
    allowedFormats: ['pdf', 'doc', 'docx'],
  });

  useEffect(() => {
    fetchAssignments();
  }, []);

  const fetchAssignments = async () => {
    try {
      const response = await assignmentAPI.getAssignments();
      setAssignments(response.data);
    } catch (error) {
      setError('Failed to fetch assignments');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      await assignmentAPI.createAssignment(formData);
      setShowModal(false);
      setFormData({
        title: '',
        description: '',
        dueDate: '',
        maxPoints: 100,
        instructions: '',
        allowedFormats: ['pdf', 'doc', 'docx'],
      });
      fetchAssignments();
    } catch (error) {
      setError('Failed to create assignment');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const isPastDue = (dueDate) => {
    return new Date(dueDate) < new Date();
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Assignments</h1>
        {user?.role === 'teacher' && (
          <Button onClick={() => setShowModal(true)}>
            Create Assignment
          </Button>
        )}
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      <Row>
        {assignments.map((assignment) => (
          <Col md={6} lg={4} key={assignment._id} className="mb-3">
            <Card>
              <Card.Body>
                <Card.Title>{assignment.title}</Card.Title>
                <Card.Text>{assignment.description}</Card.Text>
                <div className="mb-2">
                  <strong>Due:</strong> {formatDate(assignment.dueDate)}
                  {isPastDue(assignment.dueDate) && (
                    <Badge bg="danger" className="ms-2">Past Due</Badge>
                  )}
                </div>
                <div className="mb-2">
                  <strong>Points:</strong> {assignment.maxPoints}
                </div>
                {user?.role === 'student' && (
                  <Button 
                    variant="primary" 
                    size="sm"
                    // Add submission functionality here
                  >
                    Submit Assignment
                  </Button>
                )}
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Create Assignment Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create Assignment</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Due Date</Form.Label>
              <Form.Control
                type="datetime-local"
                name="dueDate"
                value={formData.dueDate}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Maximum Points</Form.Label>
              <Form.Control
                type="number"
                name="maxPoints"
                value={formData.maxPoints}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Instructions</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="instructions"
                value={formData.instructions}
                onChange={handleChange}
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" type="submit" disabled={loading}>
              Create Assignment
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
};

export default Assignments;
