// services/api.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Add a request interceptor to include the auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export const assignmentAPI = {
  getAssignments: () => api.get('/assignments'),
  
  createAssignment: (formData) => api.post('/assignments', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  }),
  
  getAssignment: (id) => api.get(`/assignments/${id}`),
  
  updateAssignment: (id, data) => api.put(`/assignments/${id}`, data),
  
  deleteAssignment: (id) => api.delete(`/assignments/${id}`),
};

export const submissionAPI = {
  submitAssignment: (assignmentId, formData) => 
    api.post('/submissions', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    }),
  
  getSubmissions: (assignmentId) => 
    api.get(`/submissions/assignment/${assignmentId}`),
  
  gradeSubmission: (submissionId, data) => 
    api.put(`/submissions/${submissionId}/grade`, data),
};

export default api;
