import React, { useState, useEffect } from 'react';
import { Card, Table, Button, Modal, Form, Badge, Alert } from 'react-bootstrap';
import { submissionAPI } from '../services/api';
import { useAuth } from '../utils/AuthContext';

const Submissions = () => {
  const [submissions, setSubmissions] = useState([]);
  const [showGradeModal, setShowGradeModal] = useState(false);
  const [selectedSubmission, setSelectedSubmission] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [gradeForm, setGradeForm] = useState({
    points: '',
    feedback: ''
  });

  const { user } = useAuth();

  useEffect(() => {
    if (user?.role === 'teacher') {
      fetchSubmissions();
    }
  }, [user]);

  const fetchSubmissions = async () => {
    try {
      const response = await submissionAPI.getSubmissions();
      setSubmissions(response.data);
    } catch (error) {
      setError('Failed to fetch submissions');
    }
  };

  const handleGradeClick = (submission) => {
    setSelectedSubmission(submission);
    setGradeForm({
      points: submission.grade?.points || '',
      feedback: submission.grade?.feedback || ''
    });
    setShowGradeModal(true);
  };

  const handleGradeSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      await submissionAPI.gradeSubmission(selectedSubmission._id, gradeForm);
      setShowGradeModal(false);
      setSelectedSubmission(null);
      fetchSubmissions();
    } catch (error) {
      setError('Failed to grade submission');
    } finally {
      setLoading(false);
    }
  };

  const handleGradeChange = (e) => {
    setGradeForm({
      ...gradeForm,
      [e.target.name]: e.target.value
    });
  };

  const getStatusBadge = (submission) => {
    const statusColors = {
      submitted: 'info',
      graded: 'success',
      late: 'danger'
    };
    
    return (
      <Badge bg={statusColors[submission.status]}>
        {submission.status.charAt(0).toUpperCase() + submission.status.slice(1)}
      </Badge>
    );
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (user?.role !== 'teacher') {
    return (
      <div className="text-center mt-5">
        <Alert variant="warning">
          You don't have permission to access this page.
        </Alert>
      </div>
    );
  }

  return (
    <div>
      <h1 className="mb-4">Student Submissions</h1>

      {error && <Alert variant="danger">{error}</Alert>}

      <Card>
        <Card.Body>
          {submissions.length === 0 ? (
            <p className="text-center text-muted">No submissions found.</p>
          ) : (
            <Table responsive striped>
              <thead>
                <tr>
                  <th>Student</th>
                  <th>Assignment</th>
                  <th>Submitted At</th>
                  <th>Status</th>
                  <th>Grade</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {submissions.map((submission) => (
                  <tr key={submission._id}>
                    <td>
                      <div>
                        {submission.student?.name}
                        {submission.student?.studentId && (
                          <div>
                            <small className="text-muted">
                              ID: {submission.student.studentId}
                            </small>
                          </div>
                        )}
                      </div>
                    </td>
                    <td>{submission.assignment?.title}</td>
                    <td>{formatDate(submission.submittedAt)}</td>
                    <td>{getStatusBadge(submission)}</td>
                    <td>
                      {submission.grade ? (
                        `${submission.grade.points}/${submission.assignment?.maxPoints}`
                      ) : (
                        'Not graded'
                      )}
                    </td>
                    <td>
                      <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={() => handleGradeClick(submission)}
                      >
                        {submission.grade ? 'Update Grade' : 'Grade'}
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Card.Body>
      </Card>

      {/* Grade Submission Modal */}
      <Modal show={showGradeModal} onHide={() => setShowGradeModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>
            Grade Submission - {selectedSubmission?.student?.name}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleGradeSubmit}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>
                Points (Max: {selectedSubmission?.assignment?.maxPoints})
              </Form.Label>
              <Form.Control
                type="number"
                name="points"
                value={gradeForm.points}
                onChange={handleGradeChange}
                min="0"
                max={selectedSubmission?.assignment?.maxPoints}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Feedback</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="feedback"
                value={gradeForm.feedback}
                onChange={handleGradeChange}
                placeholder="Provide feedback to the student..."
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button 
              variant="secondary" 
              onClick={() => setShowGradeModal(false)}
            >
              Cancel
            </Button>
            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Grading...' : 'Submit Grade'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
};

export default Submissions;
