import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Row, Col, Alert, Badge, Table } from 'react-bootstrap';
import { assignmentAPI, submissionAPI } from '../services/api';
import { useAuth } from '../utils/AuthContext';

const Assignments = () => {
  const [assignments, setAssignments] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showSubmitModal, setShowSubmitModal] = useState(false);
  const [showQuestionModal, setShowQuestionModal] = useState(false);
  const [selectedAssignment, setSelectedAssignment] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    dueDate: '',
    maxPoints: 100,
    instructions: '',
    course: '',
    questionFile: null
  });

  const [submitForm, setSubmitForm] = useState({
    submissionText: '',
    file: null
  });

  useEffect(() => {
    fetchAssignments();
  }, []);

  const fetchAssignments = async () => {
    try {
      const response = await assignmentAPI.getAssignments();
      setAssignments(response.data);
    } catch (error) {
      setError('Failed to fetch assignments');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      const submitData = new FormData();
      submitData.append('title', formData.title);
      submitData.append('description', formData.description);
      submitData.append('dueDate', formData.dueDate);
      submitData.append('maxPoints', formData.maxPoints);
      submitData.append('instructions', formData.instructions);
      submitData.append('course', formData.course);
      
      if (formData.questionFile) {
        submitData.append('questionFile', formData.questionFile);
      }

      await assignmentAPI.createAssignment(submitData);
      setShowModal(false);
      setFormData({
        title: '',
        description: '',
        dueDate: '',
        maxPoints: 100,
        instructions: '',
        course: '',
        questionFile: null
      });
      fetchAssignments();
    } catch (error) {
      setError('Failed to create assignment: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    if (e.target.name === 'questionFile') {
      setFormData({
        ...formData,
        questionFile: e.target.files[0]
      });
    } else {
      setSubmitForm({
        ...submitForm,
        file: e.target.files[0]
      });
    }
  };

  const handleSubmitAssignment = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const submitData = new FormData();
      submitData.append('submissionText', submitForm.submissionText);
      if (submitForm.file) {
        submitData.append('file', submitForm.file);
      }

      await submissionAPI.submitAssignment(selectedAssignment._id, submitData);
      setShowSubmitModal(false);
      setSelectedAssignment(null);
      setSubmitForm({
        submissionText: '',
        file: null
      });
      fetchAssignments();
    } catch (error) {
      setError('Failed to submit assignment: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitClick = (assignment) => {
    setSelectedAssignment(assignment);
    setSubmitForm({
      submissionText: '',
      file: null
    });
    setShowSubmitModal(true);
  };

  const handleViewQuestions = (assignment) => {
    setSelectedAssignment(assignment);
    setShowQuestionModal(true);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const isPastDue = (dueDate) => {
    return new Date(dueDate) < new Date();
  };

  const deleteAssignment = async (id) => {
    if (window.confirm('Are you sure you want to delete this assignment?')) {
      try {
        await assignmentAPI.deleteAssignment(id);
        fetchAssignments();
      } catch (error) {
        setError('Failed to delete assignment');
      }
    }
  };

  const getFileIcon = (filename) => {
    if (!filename) return 'ðŸ“„';
    
    const ext = filename.split('.').pop().toLowerCase();
    switch (ext) {
      case 'pdf':
        return 'ðŸ“•';
      case 'doc':
      case 'docx':
        return 'ðŸ“˜';
      case 'jpg':
      case 'jpeg':
      case 'png':
        return 'ðŸ–¼ï¸';
      case 'txt':
        return 'ðŸ“';
      default:
        return 'ðŸ“„';
    }
  };

  const downloadQuestionFile = async (assignment) => {
    try {
      if (assignment.questionFile) {
        // Create a temporary link to download the file
        const link = document.createElement('a');
        link.href = assignment.questionFile.path;
        link.download = assignment.questionFile.originalName;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      }
    } catch (error) {
      setError('Failed to download file');
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Assignments</h1>
        {user?.role === 'teacher' && (
          <Button onClick={() => setShowModal(true)}>
            Create Assignment
          </Button>
        )}
      </div>

      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}

      {user?.role === 'teacher' ? (
        <Card>
          <Card.Body>
            {assignments.length === 0 ? (
              <p className="text-center text-muted">No assignments created yet.</p>
            ) : (
              <Table responsive striped>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Su</th>
                    <th>Description</th>
                    <th>Due Date</th>
                    <th>Question File</th>
                    <th>Max Points</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {assignments.map((assignment) => (
                    <tr key={assignment._id}>
                      <td>
                        <strong>{assignment.title}</strong>
                      </td>
                      <td>{assignment.course || 'N/A'}</td>
                      <td>{assignment.description}</td>
                      <td>
                        {formatDate(assignment.dueDate)}
                        {isPastDue(assignment.dueDate) && (
                          <Badge bg="danger" className="ms-2">Past Due</Badge>
                        )}
                      </td>
                      <td>
                        {assignment.questionFile ? (
                          <Badge bg="success">
                            {getFileIcon(assignment.questionFile.originalName)} 
                            File Attached
                          </Badge>
                        ) : (
                          <Badge bg="secondary">No File</Badge>
                        )}
                      </td>
                      <td>{assignment.maxPoints}</td>
                      <td>
                        <Badge bg={isPastDue(assignment.dueDate) ? 'danger' : 'success'}>
                          {isPastDue(assignment.dueDate) ? 'Closed' : 'Active'}
                        </Badge>
                      </td>
                      <td>
                        <div className="d-flex gap-2">
                          <Button
                            variant="outline-primary"
                            size="sm"
                            onClick={() => handleViewQuestions(assignment)}
                          >
                            View
                          </Button>
                          <Button
                            variant="outline-danger"
                            size="sm"
                            onClick={() => deleteAssignment(assignment._id)}
                          >
                            Delete
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </Card.Body>
        </Card>
      ) : (
        <Row>
          {assignments.map((assignment) => (
            <Col md={6} lg={4} key={assignment._id} className="mb-3">
              <Card className="assignment-card h-100">
                <Card.Body className="d-flex flex-column">
                  <Card.Title>{assignment.title}</Card.Title>
                  {assignment.course && (
                    <Card.Subtitle className="mb-2 text-muted">
                      {assignment.course}
                    </Card.Subtitle>
                  )}
                  <Card.Text className="flex-grow-1">{assignment.description}</Card.Text>
                  
                  <div className="mb-2">
                    <strong>Due:</strong> {formatDate(assignment.dueDate)}
                    {isPastDue(assignment.dueDate) && (
                      <Badge bg="danger" className="ms-2">Past Due</Badge>
                    )}
                  </div>
                  
                  <div className="mb-2">
                    <strong>Points:</strong> {assignment.maxPoints}
                  </div>
                  
                  {assignment.instructions && (
                    <div className="mb-2">
                      <strong>Instructions:</strong>
                      <small className="d-block text-muted">
                        {assignment.instructions}
                      </small>
                    </div>
                  )}

                  {assignment.questionFile && (
                    <div className="mb-3">
                      <strong>Question File:</strong>
                      <div className="d-flex align-items-center mt-1">
                        <span className="me-2">{getFileIcon(assignment.questionFile.originalName)}</span>
                        <small className="text-truncate me-2">
                          {assignment.questionFile.originalName}
                        </small>
                        <Button
                          variant="outline-primary"
                          size="sm"
                          onClick={() => handleViewQuestions(assignment)}
                        >
                          View
                        </Button>
                      </div>
                    </div>
                  )}

                  <div className="mt-auto">
                    <Button 
                      variant="primary" 
                      size="sm"
                      onClick={() => handleSubmitClick(assignment)}
                      disabled={isPastDue(assignment.dueDate)}
                      className="w-100"
                    >
                      {isPastDue(assignment.dueDate) ? 'Past Due' : 'Submit Assignment'}
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}

      {/* Create Assignment Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Create Assignment</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Title *</Form.Label>
                  <Form.Control
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Course</Form.Label>
                  <Form.Control
                    type="text"
                    name="course"
                    value={formData.course}
                    onChange={handleChange}
                    placeholder="e.g., Mathematics, Physics"
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Description *</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Due Date *</Form.Label>
                  <Form.Control
                    type="datetime-local"
                    name="dueDate"
                    value={formData.dueDate}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Maximum Points *</Form.Label>
                  <Form.Control
                    type="number"
                    name="maxPoints"
                    value={formData.maxPoints}
                    onChange={handleChange}
                    min="1"
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Instructions</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="instructions"
                value={formData.instructions}
                onChange={handleChange}
                placeholder="Additional instructions for students..."
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Upload Question File</Form.Label>
              <Form.Control
                type="file"
                name="questionFile"
                onChange={handleFileChange}
                accept=".pdf,.doc,.docx,.txt,.jpg,.jpeg,.png"
              />
              <Form.Text className="text-muted">
                Supported formats: PDF, DOC, DOCX, TXT, JPG, PNG (Max: 10MB)
              </Form.Text>
              {formData.questionFile && (
                <div className="mt-2">
                  <Badge bg="info">
                    Selected: {formData.questionFile.name}
                  </Badge>
                </div>
              )}
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Creating...' : 'Create Assignment'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* Submit Assignment Modal */}
      <Modal show={showSubmitModal} onHide={() => setShowSubmitModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Submit Assignment: {selectedAssignment?.title}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmitAssignment}>
          <Modal.Body>
            {selectedAssignment && (
              <>
                <div className="mb-3">
                  <strong>Description:</strong>
                  <p>{selectedAssignment.description}</p>
                </div>
                
                {selectedAssignment.instructions && (
                  <div className="mb-3">
                    <strong>Instructions:</strong>
                    <p>{selectedAssignment.instructions}</p>
                  </div>
                )}
                
                <div className="mb-3">
                  <strong>Due Date:</strong>
                  <p>{formatDate(selectedAssignment.dueDate)}</p>
                </div>

                {selectedAssignment.questionFile && (
                  <div className="mb-3 p-3 border rounded">
                    <strong>Question File:</strong>
                    <div className="d-flex align-items-center mt-2">
                      <span className="me-2 fs-5">
                        {getFileIcon(selectedAssignment.questionFile.originalName)}
                      </span>
                      <span className="me-3">{selectedAssignment.questionFile.originalName}</span>
                      <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={() => downloadQuestionFile(selectedAssignment)}
                      >
                        Download
                      </Button>
                    </div>
                  </div>
                )}
              </>
            )}

            <Form.Group className="mb-3">
              <Form.Label>Submission Text *</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="submissionText"
                value={submitForm.submissionText}
                onChange={(e) => setSubmitForm({
                  ...submitForm,
                  submissionText: e.target.value
                })}
                placeholder="Enter your submission text here..."
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Upload Additional File (Optional)</Form.Label>
              <Form.Control
                type="file"
                onChange={handleFileChange}
                accept=".pdf,.doc,.docx,.txt,.jpg,.jpeg,.png"
              />
              <Form.Text className="text-muted">
                Supported formats: PDF, DOC, DOCX, TXT, JPG, PNG (Max: 10MB)
              </Form.Text>
              {submitForm.file && (
                <div className="mt-2">
                  <Badge bg="info">
                    Selected: {submitForm.file.name}
                  </Badge>
                </div>
              )}
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button 
              variant="secondary" 
              onClick={() => setShowSubmitModal(false)}
            >
              Cancel
            </Button>
            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Submitting...' : 'Submit Assignment'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* View Questions Modal */}
      <Modal show={showQuestionModal} onHide={() => setShowQuestionModal(false)} size="xl">
        <Modal.Header closeButton>
          <Modal.Title>
            {selectedAssignment?.title} - Questions
            {selectedAssignment?.course && ` (${selectedAssignment.course})`}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedAssignment && (
            <>
              <div className="mb-4">
                <h5>Description</h5>
                <p>{selectedAssignment.description}</p>
              </div>

              {selectedAssignment.instructions && (
                <div className="mb-4">
                  <h5>Instructions</h5>
                  <p>{selectedAssignment.instructions}</p>
                </div>
              )}

              <div className="mb-4">
                <h5>Assignment Details</h5>
                <Row>
                  <Col md={6}>
                    <strong>Due Date:</strong> {formatDate(selectedAssignment.dueDate)}
                  </Col>
                  <Col md={6}>
                    <strong>Max Points:</strong> {selectedAssignment.maxPoints}
                  </Col>
                </Row>
              </div>

              {selectedAssignment.questionFile ? (
                <div className="text-center">
                  <h5>Question File</h5>
                  <div className="mb-3">
                    <span className="fs-1">{getFileIcon(selectedAssignment.questionFile.originalName)}</span>
                    <h6>{selectedAssignment.questionFile.originalName}</h6>
                  </div>
                  
                  {/* Display PDF or image files inline */}
                  {selectedAssignment.questionFile.originalName.toLowerCase().endsWith('.pdf') ? (
                    <div className="border rounded p-3">
                      <iframe 
                        src={selectedAssignment.questionFile.path} 
                        width="100%" 
                        height="500px" 
                        title="Question PDF"
                        className="border-0"
                      />
                    </div>
                  ) : selectedAssignment.questionFile.originalName.match(/\.(jpg|jpeg|png)$/i) ? (
                    <div className="border rounded p-3 text-center">
                      <img 
                        src={selectedAssignment.questionFile.path} 
                        alt="Question" 
                        style={{ maxWidth: '100%', maxHeight: '500px' }}
                        className="img-fluid"
                      />
                    </div>
                  ) : (
                    <div className="border rounded p-4 text-center">
                      <p>This file type cannot be previewed. Please download to view.</p>
                    </div>
                  )}
                  
                  <div className="mt-3">
                    <Button
                      variant="primary"
                      onClick={() => downloadQuestionFile(selectedAssignment)}
                    >
                      Download File
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="text-center text-muted p-4">
                  <h5>No Question File Available</h5>
                  <p>This assignment doesn't have an attached question file.</p>
                </div>
              )}
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowQuestionModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Assignments;
